<?php

namespace KHVT\AdminThemeManager\Application\Model;

use OxidEsales\Eshop\Core\Exception\StandardException;
use OxidEsales\Eshop\Core\Registry;
use OxidEsales\Eshop\Core\SettingsHandler;
use OxidEsales\Eshop\Core\Theme;

/**
 * Class AdminTheme
 *
 * @package KHVT\AdminThemeManager\Model
 */
class AdminTheme extends Theme
{
    /**
     *  default oxid admin theme ident
     */
    const oxAdminStandardTheme = 'admin';

    /**
     * Set theme as active
     *
     * @throws StandardException
     */
    public function activate()
    {
        $sError = $this->checkForActivationErrors();
        if ($sError) {
            /** @var StandardException $oException */
            $oException = oxNew(StandardException::class, $sError);
            throw $oException;
        }

        $this->saveConfiguration($this->getInfo('parentTheme'));

        /** @var SettingsHandler::class $settingsHandler */
        $settingsHandler = Registry::get(SettingsHandler::class);
        $settingsHandler->setModuleType('theme')->run($this);
    }

    /**
     * Get active themes list.
     * Examples:
     *      if admin theme is active we will get ['admin']
     *      if admin is extended by some other we will get ['admin', 'extending_theme']
     *      if new_admin theme is active we will get ['new_admin']
     *
     * @return array
     */
    public function getActiveThemesList()
    {
        $activeThemeList = [];
        if (false == $this->isAdmin()) {
            return $activeThemeList;
        }

        $adminTheme = $this->getConfig()->getConfigParam('sAdminTheme');
        if ($adminTheme) {
            $activeThemeList[] = $adminTheme;
        }

        $adminCustomTheme = $this->getConfig()->getConfigParam('sAdminCustomTheme');
        if ($adminCustomTheme) {
            $activeThemeList[] = $adminCustomTheme;
        }

        if (empty($activeThemeList)) {
            $activeThemeList[] = self::oxAdminStandardTheme;
        }

        return $activeThemeList;
    }

    /**
     * Return current active/custom theme or oxid standard admin theme
     *
     * @return string
     */
    public function getActiveThemeId()
    {
        $adminCustomTheme = $this->getConfig()->getConfigParam('sAdminCustomTheme');
        if ($adminCustomTheme) {
            return $adminCustomTheme;
        }

        $adminTheme = $this->getConfig()->getConfigParam('sAdminTheme');
        if ($adminTheme) {
            return $adminTheme;
        }

        return self::oxAdminStandardTheme;
    }

    /**
     * returns list with loaded admin themes
     *
     * @return array
     */
    public function getList()
    {
        $this->_aThemeList = [];
        $sOutDir           = $this->getConfig()->getViewsDir();
        foreach (glob($sOutDir."*", GLOB_ONLYDIR) as $sDir) {
            /** @var self $adminTheme */
            $adminTheme    = oxNew(self::class);
            $directoryName = basename($sDir);

            if ($adminTheme->load($directoryName)) {
                $this->_aThemeList[$sDir] = $adminTheme;
            }
        }

        return $this->_aThemeList;
    }

    /**
     * Load admin theme info
     * can load oxid standard admin theme without adminTheme.php
     *
     * @param string $adminThemeId admin theme id
     *
     * @return bool
     */
    public function load($adminThemeId)
    {
        $filePath = $this->getConfig()->getViewsDir().$adminThemeId."/adminTheme.php";
        if (file_exists($filePath) && is_readable($filePath)) {
            $themeParameter = [];
            include $filePath;
            $this->_aTheme           = $themeParameter;
            $this->_aTheme['id']     = $adminThemeId;
            $this->_aTheme['active'] = ($this->getActiveThemeId() == $adminThemeId);

            return true;
        } elseif ($adminThemeId === self::oxAdminStandardTheme) {
            $this->loadOxAdmin();

            return true;
        }

        return false;
    }

    /**
     * @return object|Theme|null
     */
    public function getParent()
    {
        $sParent = $this->getInfo('parentTheme');
        if (!$sParent) {
            return null;
        }
        $oTheme = oxNew(self::class);
        if ($oTheme->load($sParent)) {
            return $oTheme;
        }

        return null;
    }

    /**
     * @param $sParent
     */
    public function saveConfiguration($sParent = null)
    {
        $adminTheme       = $this->getId();
        $adminCustomTheme = '';

        if ($sParent) {
            $adminCustomTheme = $adminTheme;
            $adminTheme       = $sParent;
        }

        $this->getConfig()->saveShopConfVar("str", 'sAdminTheme', $adminTheme);
        $this->getConfig()->saveShopConfVar("str", 'sAdminCustomTheme', $adminCustomTheme);

    }

    /**
     * Load oxid standard admin theme info
     *
     * @return bool
     */
    public function loadOxAdmin()
    {
        $this->_aTheme = [
            'id'          => self::oxAdminStandardTheme,
            'active'      => ($this->getActiveThemeId() == self::oxAdminStandardTheme),
            'title'       => 'Oxid Standard Admin',
            'description' => 'This is OXID eShop\'s official admin theme.',
            'thumbnail'   => 'theme.png',
            'version'     => '',
            'author'      => '<a href="http://www.oxid-esales.com" title="OXID eSales AG">OXID eSales AG</a>',
            'settings'    => array(),
        ];

        return true;
    }

    /**
     * Returns a standard picture, if no thumbnail is defined
     *
     *
     * @return string
     */
    public function getThumbnailUrl()
    {
        $filePath = $this->getConfig()->getOutDir(true)
            .$this->getActiveThemeId()
            .DIRECTORY_SEPARATOR
            .$this->getInfo('thumbnail');
        if (file_exists($filePath) && is_readable($filePath)) {
            return $this->getConfig()->getOutDir(false)
                .$this->getActiveThemeId()
                .DIRECTORY_SEPARATOR
                .$this->getInfo('thumbnail');
        }

        return "data:image/png;base64,";
    }

    /**
     *
     *
     * @return array|false
     */
    public function getTemplates()
    {
        $templatePath = $this->getConfig()->getTemplateDir(true);

        $templates = glob($templatePath.'*.tpl');
        if (is_array($templates)) {
            array_walk(
                $templates,
                function (&$item) {
                    $item = basename($item);
                }
            );

            return $templates;
        }

        return [];
    }
}
